<?php
$create_users_sql = "CREATE TABLE `hanyun`.`oms_users` (`id` INT(10) NOT NULL AUTO_INCREMENT PRIMARY KEY, `username` VARCHAR(40) NOT NULL, `password` VARCHAR(50) NOT NULL, `created` DATETIME NULL DEFAULT NULL, `email` VARCHAR(255) NOT NULL, `tel` VARCHAR(20) NULL DEFAULT NULL, UNIQUE (`username`, `email`)) ENGINE = MyISAM;";
$create_orders_sql = "CREATE TABLE `hanyun`.`oms_orders` (`id` INT(20) NOT NULL AUTO_INCREMENT PRIMARY KEY, `order_number` VARCHAR(50) NOT NULL, `user_address` VARCHAR(255) NOT NULL, `user_name` VARCHAR(40) NOT NULL, `user_email` VARCHAR(255) NOT NULL, `user_tel` VARCHAR(20) NOT NULL, `item_id` INT(20) NOT NULL, `created` DATETIME NOT NULL, `checked` BOOL NOT NULL DEFAULT \'0\', `checked_by` VARCHAR(40) NOT NULL, UNIQUE (`order_number`)) ENGINE = MyISAM;";
$create_articles_sql =  "CREATE TABLE `hanyun`.`oms_articles` (`id` INT(20) NOT NULL AUTO_INCREMENT PRIMARY KEY, `title` VARCHAR(255) NOT NULL, `introtext` TEXT NOT NULL, `created` DATETIME NOT NULL, `created_by` INT(20) NOT NULL, `modified` DATETIME NOT NULL, `modified_by` INT(20) NOT NULL) ENGINE = MyISAM;";
$create_item_sql = "CREATE TABLE `hanyun`.`oms_items` (`id` INT(20) NOT NULL AUTO_INCREMENT PRIMARY KEY, `name` VARCHAR(40) NOT NULL, `isp` VARCHAR(40) NOT NULL, `bandwith` VARCHAR(20) NOT NULL, `cpu` VARCHAR(20) NOT NULL, `memory` VARCHAR(20) NOT NULL, `disk` VARCHAR(20) NOT NULL, `os` VARCHAR(20) NOT NULL, `images` VARCHAR(255) DEFAULT NULL, `images_dir` varchar(255) DEFAULT NULL, `created` DATETIME NOT NULL, `created_by` VARCHAR(40) NOT NULL, `modified` DATETIME NOT NULL, `modified_by` VARCHAR(40) NOT NULL, `price` DECIMAL(10,2) NOT NULL) ENGINE = MyISAM;";
?>